            var audit = new AuditModel(
                LoggedUser.Id,
                LoggedUser.Name,
                AuditWhat.See,
                $"Entrando en Home games",
                $"Entrando en Home games",
                Common.Mongo.ResponseStatus.Ok
                );
            await ApiService.Audits.InsertAsync(audit);
            _videorand = new Random().Next(0, _videos.Count);

            await GetLists();
        }

        private async Task GetLists()
        {
            IsLoading = true;
            await InvokeAsync(StateHasChanged);

            string paraams = "&dates=2025-06-01,2025-09-11&ordering=-rating&page_size=20";
               var x = await DController.GetData(await HttpApiService.Games.GetSearchGameModelByUrlAsync(paraams, LoggedUser));
            PopularGames = x.Results;

            var y = await DController.GetData(await HttpApiService.Games.GetSearchGameModelByUrlAsync("", LoggedUser));
            EverGames = y.Results;

            IsLoading = false;
            await InvokeAsync(StateHasChanged);
        }

        #endregion

        private async Task ChangeTrendingGames(string text)
        {
            if (text == "🔥 Populares")
            {
                _trendingGame = text;
                string paraams = "&dates=2025-06-01,2025-09-11&ordering=-rating&page_size=20";
                var x = await DController.GetData(await HttpApiService.Games.GetSearchGameModelByUrlAsync(paraams, LoggedUser));
                PopularGames = x.Results;
            }
            else if(text == "🆕 Proximos Lanzamientos")
